import React, { Component } from "react";
import { Link } from "react-router-dom";
class Read extends Component {
  constructor() {
    super();
    this.state = {
      _id: null,
      name: null,
      name2: null,
      name3: null,
      __v: null,
      _links: null
    };
  }
  async fetchResult() {
    const res = await fetch(
      `http://184.72.97.138:9000/tutorial/${this.props.match.params.id}`
    );
    const json = await res.json();
    this.setState(json);
  }
  componentDidMount() {
    this.fetchResult();
  }
  render() {
    return (
      <React.Fragment>
        <div className="row">
          <div className="col">
            <h1>Viewing {this.state._id}</h1>
          </div>
        </div>
        <div className="row">
          <br />
          <div className="col">
            <h1>Details:</h1>
            <p>ID: {this.state._id}</p>
            <p>Name: {this.state.name}</p>
            <p>Name2: {this.state.name2}</p>
            <p>Name3: {this.state.name3}</p>
            <p>_V: {this.state.__v}</p>
          </div>
          <div className="col">
            <h1>Options:</h1>
            <div className="">
              <div className="btn-group">
                <Link to={this.state._id + "/edit"} className="btn btn-success">
                  Edit item
                </Link>
                <Link
                  to={this.state._id + "/delete"}
                  className="btn btn-danger"
                >
                  Delete item
                </Link>
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Read;
