import React from 'react';
import { Card } from './Card';

export class GameDex extends React.Component {

    constructor() {
        super()
        this.state = { 
            game: []
        }
    }

    componentDidMount() {
        this.loadGame()
        }

        async loadGame() {
            const response = await fetch(`http://145.24.222.167:8000/games`)
            const json = await response.json()
            console.log(json.items)
            this.setState({game: json.items})
        }    

    render() {

        let thumbs = this.state.game.map((singleGame, i) =>
            <Card key={i} name={singleGame.name} uri={singleGame.uri}/>
        )


        return (
            <div className="gamedex">
                <div>
                    <button>Load next 6 games</button>
                </div>
                <div>
                    Caught: {this.state.caught}
                </div>
                <div className="thumbnails">
                    {thumbs}
                </div>
            </div>
        )   
    }
}
