import React, { Component } from "react";
import { Link, Redirect } from "react-router-dom";
class Delete extends Component {
  constructor() {
    super();
    this.state = {
      _id: null,
      name: null,
      name2: null,
      name3: null,
      __v: null,
      _links: null,
      deleted: false
    };
    this.delete = this.delete.bind(this);
  }
  async fetchResult() {
    const res = await fetch(
      `http://145.24.222.167:8000/games/${this.props.match.params.id}`
    );
    const json = await res.json();
    this.setState(json);
  }
  componentDidMount() {
    this.fetchResult();
  }
  async delete() {
    await fetch(
      `http://145.24.222.167:8000/games/${this.props.match.params.id}`,
      {
        method: "DELETE",
        headers: { "Content-Type": "application/json" }
      }
    );
    this.setState({ deleted: true });
  }
  render() {
    let redirect;
    if (this.state.deleted) {
      redirect = <Redirect to="/"></Redirect>;
    }
    return (
      <div className="row">
        {redirect}
        <div className="col">
          <h1>You are about to delete {this.state._id} , are you sure?</h1>
          <div className="btn-group">
            <button className="btn btn-success" onClick={this.delete}>
              Yes
            </button>
            <Link to={this.state._id + "/delete"} className="btn btn-danger">
              Nono
            </Link>
          </div>
        </div>
      </div>
    );
  }
}

export default Delete;
