import React, { Component } from "react";
import { Link } from "react-router-dom";

class Main extends Component {
  constructor() {
    super();
    this.state = {
      items: [],

    };
    this.updateResults = this.updateResults.bind(this);
  }
  componentDidMount() {
    this.fetchResults();
  }
  async fetchResults() {
    const res = await fetch(
      `http://145.24.222.167:8000/games`
    );
    const json = await res.json();
    this.setState(json);
  }

  render() {
    let thumbs = this.state.game.map((singleGame) => {
        return(
            <tr key={singleGame.id}>
                <td>{singleGame.id}</td>
                <td>{singleGame.name}</td>
                <td>{singleGame.genre}</td>
                <td>{singleGame.gameconsole}</td>
                <td>
                <Link to={items._id} className="btn btn-primary">
            View item
          </Link>
                </td>
            </tr>
        )


    });

return (
    <div>
        <h1>Showing {this.state.items.length}</h1>
        <div className="row">
          <div className="col">
            <ul className="list-group">{items}</ul>
          </div>
        <Table>
            <thead>
                <tr>
                    {thumbs}
                </tr>
            </thead>
        </Table>
    </div>
    </div>
)   
}
}

export default Main;
