import React from 'react';
import { Card } from './Card';

export class GameDex extends React.Component {

    constructor() {
        super()
        this.state = { 
            game: []
        }
    }

    componentDidMount() {
        this.loadGame()
        }

        async loadGame() {
            const response = await fetch(`http://145.24.222.167:8000/games`)
            const json = await response.json()
            console.log(json.items)
            this.setState({game: json.items})
        }    

    render() {

        let thumbs = this.state.game.map((singleGame, i) =>
            <Card key={i} name={singleGame.name} uri={singleGame.uri}/>
        )

        return (
            <div className="gamedex">

            <Table>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Title</th>
                        <th>Rating</th>
                        <th>Actions</th>
                    </tr>
                </thead>

                <tbody>
                    <tr>
                        <td>
                        <button color="success" size="sm" className="mr-2">Edit</button>
                        <button color="success" size="sm">Delete</button>
                        </td>
                    </tr>
                </tbody>   
            </Table>
            </div> 
        )   
    }
}
