import React, { Component } from "react";
import { Link } from "react-router-dom";
class Read extends Component {
  constructor() {
    super();
    this.state = {
      id: null,
      name: null,
      genre: null,
      gameconsole: null,
    };
  }
  async fetchResult() {
    const res = await fetch(
      `http://145.24.222.167:8000/games/${this.props.match.params.id}`
    );
    const json = await res.json();
    this.setState(json);
  }
  componentDidMount() {
    this.fetchResult();
  }
  render() {
    return (
      <React.Fragment>
        <div className="row">
          <div className="col">
            <h1>Viewing {this.state._id}</h1>
          </div>
        </div>
        <div className="row">
          <br />
          <div className="col">
            <h1>Details:</h1>
            <p>ID: {this.state._id}</p>
            <p>Name: {this.state.name}</p>
            <p>Name2: {this.state.genre}</p>
            <p>Name3: {this.state.gameconsole}</p>

          </div>
          <div className="col">
            <h1>Options:</h1>
            <div className="">
              <div className="btn-group">
                <Link to={this.state._id + "/edit"} className="btn btn-success">
                  Edit item
                </Link>
                <Link
                  to={this.state._id + "/delete"}
                  className="btn btn-danger"
                >
                  Delete item
                </Link>
              </div>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Read;
