import React from 'react';

export class Create extends React.Component {

        constructor() {
          super();
          this.state = {
            done: false,
            error: false
          };

          this.handleChange = this.handleChange.bind(this);
          this.add = this.add.bind(this);
        }

        handleChange(e) {
          this.setState({ [e.target.name]: e.target.value });
        }

        async add() {
          const response = await fetch(`http://184.72.97.138:9000/tutorial/`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(this.state)
          });
          const json = await res.status;
          const msg = await res.json();
          if (json === 201) {
            this.setState({ success: "Item created succesfully" });
          } else {
            this.setState({
              error: "Item not created succesfully. Reason why: " + msg.message
            });
          }
        }



        render() {
          let success;
          if (this.state.success) {
            success = (
              <div className="alert alert-success" role="alert">
                {this.state.success}
              </div>
            );
          }
          let error;
          if (this.state.error) {
            error = (
              <div className="alert alert-danger" role="alert">
                {this.state.error}
              </div>
            );
          }
          return (
            <React.Fragment>
              <div className="row">
                <div className="col">
                  <h1>Make a new item:</h1>
                  {success}
                  {error}
                </div>
              </div>
              <div className="row">
                <br />
                <div className="col">
                  <p>
                    Name:{" "}
                    <input
                      onChange={this.handleChange}
                      type="text"
                      name="name"
                      value={this.state.name ? this.state.name : ""}
                      id="name"
                      className="form-control"
                    ></input>
                  </p>
                  <p>
                    Name2:{" "}
                    <input
                      onChange={this.handleChange}
                      type="text"
                      name="name2"
                      value={this.state.name2 ? this.state.name2 : ""}
                      id="name2"
                      className="form-control"
                    ></input>
                  </p>
                  <p>
                    Name3:{" "}
                    <input
                      onChange={this.handleChange}
                      type="text"
                      name="name3"
                      value={this.state.name3 ? this.state.name3 : ""}
                      id="name3"
                      className="form-control"
                    ></input>
                  </p>
                  <p>
                    <input
                      type="submit"
                      className="btn btn-primary"
                      onClick={this.submit}
                      value="Create item"
                    ></input>
                  </p>
                </div>
              </div>
            </React.Fragment>
          );
        }
      }

      export default Create;
