import React, { Component } from "react";
class Pagination extends Component {
  constructor(props) {
    super();
    this.state = {
      currentPage: null,
      currentItems: null,
      totalPages: null,
      totalItems: null,
      start: props.start,
      limit: props.limit
    };
  }
  componentWillReceiveProps(nextProps) {
    this.setState(nextProps.data);
    this.setState(nextProps);
  }
  next() {
    if (
      Math.ceil(this.state.start / this.state.limit) + 1 <
      this.state.totalPages
    ) {
      let n = {
        start: this.state.start + this.state.limit,
        limit: this.state.limit
      };
      this.props.updateResults(n);
    }
  }
  previous() {
    if (Math.ceil(this.state.start / this.state.limit) > 0) {
      let n = {
        start: this.state.start - this.state.limit,
        limit: this.state.limit
      };
      this.props.updateResults(n);
    }
  }
  page(number) {
    if (number != Math.ceil(this.state.start / this.state.limit)) {
      let n = {
        start: this.state.limit * number,
        limit: this.state.limit
      };
      console.log(n);
      this.props.updateResults(n);
    }
  }
  render() {
    let btns = [];
    for (let key = 0; key < this.state.totalPages; key++) {
      btns.push(
        <li
          className={`page-item ${
            key === Math.ceil(this.state.start / this.state.limit)
              ? "active"
              : ""
          }`}
          key={key}
          onClick={() => this.page(key)}
        >
          <a className="page-link">{key + 1}</a>
        </li>
      );
    }
    btns.unshift(
      <li className="page-item" key="prev">
        <a className="page-link" onClick={() => this.previous()}>
          Previous page
        </a>
      </li>
    );
    btns.push(
      <li className="page-item" key="next">
        <a className="page-link" onClick={() => this.next()}>
          Next page
        </a>
      </li>
    );
    return (
      <div className="col">
        <br />
        <nav className="Page navigation example">
          <ul className="pagination">{btns}</ul>
        </nav>
      </div>
    );
  }
}

export default Pagination;
