import React from 'react';
import { Card } from './Card';
import { Modal } from 'reactstrap'
import { ModalHeader } from 'reactstrap'
import { ModalBody } from 'reactstrap'
import { ModalFooter } from 'reactstrap'
import { Table } from 'reactstrap'
import { Button } from 'reactstrap'

export class GameDex extends React.Component {

    constructor() {
        super()
        this.state = { 
            game: []
        ,
        editGameData: {
            id: '',
            name: '',
            genre: '',
            gameconsole: ''
        }
    }
    }

    componentDidMount() {
        this.loadGame()
        }

    toggleNewBookModal() {
        this.setState({
            newGameModal: true
        });
    }

        async loadGame() {
            const response = await fetch(`http://145.24.222.167:8000/games`)
            const json = await response.json()
            console.log(json.items)
            this.setState({game: json.items})
        }    

        editGame(id, name, genre, gameconsole) {
            console.log(name)
        }

    render() {

        let thumbs = this.state.game.map((singleGame) => {
            return(
                <tr key={singleGame.id}>
                    <td>{singleGame.id}</td>
                    <td>{singleGame.name}</td>
                    <td>{singleGame.genre}</td>
                    <td>{singleGame.gameconsole}</td>
                    <td>
                        <Button color="success" size="sm" className="mr-2" 
                        onClick={this.editGame.bind(this, singleGame.id, singleGame.name,singleGame.genre,singleGame.gameconsole)}>
                            Edit</Button>
                    </td>
                </tr>
            )
            });

        return (
            <div className="gamedex">
<Modal isOpen={this.state.editGameModal} toggle={this.state.editGameModal.bind(this)}>
        <ModalHeader toggle={this.state.editGameModal.bind(this)}>Edit game</ModalHeader>
        <ModalBody>
            <formgroup>
          <label for="name">Name</label>
          <input id="name" value={this.state.editGameData.name} onChange={(e) => {
              let { editGameData} = this.state;

              editGameData.name=e.target.value;
              this.setState({editGameData});
          }} />
          </formgroup>
          <formgroup>
          <label for="genre">Genre</label>
          <input id="genre" value={this.state.editGameData.genre} onChange={(e) => {
              let { editGameData} = this.state;

              editGameData.genre=e.target.value;
              this.setState({editGameData});
          }} />

            </formgroup>
            <formgroup>
          <label for="gameconsole">Genre</label>
          <input id="gameconsole" value={this.state.editGameData.gameconsole} onChange={(e) => {
              let { editGameData} = this.state;

              editGameData.gameconsole=e.target.value;
              this.setState({editGameData});
          }} />
</formgroup>
</ModalBody>
        <ModalFooter>
          <Button color="primary" onClick={this.updateGame}>Do Something</Button>{' '}
          <Button color="secondary" onClick={toggle}>Cancel</Button>
        </ModalFooter>
      </Modal>
                <Table>
                    <thead>
                        <tr>
                            {thumbs}
                        </tr>
                    </thead>
                </Table>
            </div>
        )   
    }
}
