import React from 'react';
import { Card } from './Card';
import { Table } from 'reactstrap'
import { Button } from 'reactstrap'

export class GameDex extends React.Component {

    constructor() {
        super()
        this.state = { 
            game: []
        }
    }

    componentDidMount() {
        this.loadGame()
        }

        async loadGame() {
            const response = await fetch(`http://145.24.222.167:8000/games`)
            const json = await response.json()
            console.log(json.items)
            this.setState({game: json.items})
        }    

    render() {

        let thumbs = this.state.game.map((singleGame) => {
            return(
                <tr key={singleGame.id}>
                    <td>{singleGame.id}</td>
                    <td>{singleGame.name}</td>
                    <td>{singleGame.genre}</td>
                    <td>{singleGame.gameconsole}</td>
                    <td>
                        <Button color="success" size="sm" className="mr-2">Edit</Button>
                        <Button color="danger" size="sm">Delete</Button>
                    </td>
                </tr>
            )
            });


        return (
            <div className="gamedex">
                <Button color="primary" onClick={toggleNewGameModal.bind(this)}>Add game</Button>
                    <Modal isOpen={this.state.newGameModal} toggle={toggleNewGameModal.bind(this)} className={this.props.className}>
                        <ModalHeader toggle={toggleNewGameModal.bind(this)}>Modal title</ModalHeader>
                    <ModalBody>
                        Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                    </ModalBody>
                    <ModalFooter>
                        <Button color="primary" onClick={this.toggle}>Do Something</Button>{' '}
                        <Button color="secondary" onClick={this.toggle}>Cancel</Button>
                    </ModalFooter>
                    </Modal>


                <Table>
                    <thead>
                        <tr>
                            {thumbs}
                        </tr>
                    </thead>
                </Table>
            </div>
        )   
    }
}
