import React from 'react';
import { Card } from './Card';
import { Modal } from 'reactstrap'
import { ModalHeader } from 'reactstrap'
import { ModalBody } from 'reactstrap'
import { ModalFooter } from 'reactstrap'
import { Table } from 'reactstrap'
import { Button } from 'reactstrap'
import { Input } from 'reactstrap'
import { FormGroup } from 'reactstrap'
import { Label } from 'reactstrap'
import  axios  from 'axios'

export class Info extends React.Component {

    constructor() {
        super()
        this.state = { 
            game: []
        ,
        showGameData: {
            _id: '',
            name: '',
            genre: '',
            gameconsole: ''
        },
        showGameModal: false
    }

    }

    componentDidMount() {
        this.loadGame()
        }

    toggleShowGameModal() {
        this.setState({
            showGameModal: ! this.state.showGameModal
        });
    }

        async loadGame() {
            const response = await fetch(`http://145.24.222.167:8000/games`)
            const json = await response.json()
            console.log(json.items)
            this.setState({game: json.items})
        }    


            updateGame(){
                const {name, genre, gameconsole} = this.state.showGameData;
                 axios.put('http://145.24.222.167:8000/games/' + this.state.showGameData._id, {
                     name, genre, gameconsole}).then((response) => {
                         this.loadGame();
                     });
                 }

                 showGame(_id, name, genre, gameconsole) {
                    this.setState({
                        showGameData: { _id, name, genre, gameconsole}, editGameModal: ! this.state.showGameModal
                    });
                }

                deleteGame(_id) {
                    axios.delete('http://145.24.222.167:8000/games/' + _id).then((response)=>{
                        this.loadGame();
                    });
                }





    render() {

        let thumbs = this.state.game.map((singleGame) => {
            return(
                <tr key={singleGame._id}>
                    <td>{singleGame._id}</td>

                    <td>


                    </td>
                    <Button color="success" size="sm" className="mr-2" 
                        onClick={this.showGame.bind(this, singleGame._id, singleGame.name,singleGame.genre,singleGame.gameconsole)}>
                            Show data</Button>

                </tr>

            )
            });

        return (
            <div>
            <Modal isOpen={this.state.showGameModal} toggle={this.toggleShowGameModal.bind(this)}>
            <ModalHeader toggle={this.toggleShowGameModal.bind(this)}>Show Data</ModalHeader>
            <ModalBody>
                <FormGroup>
              <Label for="name">Name</Label>
              <Input id="name" value={this.state.showGameData.name} onChange={(e) => {
                  let { showGameData} = this.state;

                  showGameData.name=e.target.value;
                  this.setState({showGameData});
              }} />
              </FormGroup>
              <FormGroup>
              <Label for="genre">Genre</Label>
              <Input id="genre" value={this.state.showGameData.genre} onChange={(e) => {
                  let { showGameData} = this.state;

                  showGameData.genre=e.target.value;
                  this.setState({showGameData});
              }} />

                </FormGroup>
                <FormGroup>
              <Label for="gameconsole">Genre</Label>
              <Input id="gameconsole" value={this.state.showGameData.gameconsole} onChange={(e) => {
                  let { showGameData} = this.state;

                  showGameData.gameconsole=e.target.value;
                  this.setState({showGameData});
              }} />
    </FormGroup>
    </ModalBody>
            <ModalFooter>
              <Button color="secondary" onClick={this.toggleShowGameModal.bind(this)}>Cancel</Button>
            </ModalFooter>
          </Modal>

                <Table>
                    <thead>
                        <tr>
                            {thumbs}

                        </tr>
                    </thead>
                </Table>
            </div>
        )   
    }
}
