import React from 'react';
import { Modal, InputGroup } from 'reactstrap'
import { ModalHeader } from 'reactstrap'
import { ModalBody } from 'reactstrap'
import { ModalFooter } from 'reactstrap'
import { Table } from 'reactstrap'
import { Button } from 'reactstrap'
import {Input, FormGroup, Label} from 'reactstrap'

export class GameDex extends React.Component {

    constructor() {
        super()
        this.state = { 
            game: [],
            newGameData: {
                title: '',
                genre: '',
                gameconsole: ''
            },
            newGameModel: false
        }
    }

    componentDidMount() {
        this.loadGame()
        }

        async loadGame() {
            const response = await fetch(`http://145.24.222.167:8000/games`)
            const json = await response.json()
            console.log(json.items)
            this.setState({game: json.items})
        }   

        toggleNewGameModal() {
            this.setState({
                newGameModal: ! this.state.newGameModal
            });
        }

         async postData () {
            const data = { title: 'title',
                            genre: 'genre',
                            gameconsole: 'gameconsole' };

const response = await fetch('http://145.24.222.167:8000/games', {
  method: 'POST', // or 'PUT'
  headers: {
    'Content-Type': 'application/json',
  },
  body: JSON.stringify(data),
})
.then((response) => response.json())
.then((data) => {
  console.log('Success:', data);
})
.catch((error) => {
  console.error('Error:', error);
})
const json = await response.json()
this.setState({game: json.items});
        }

    render() { 

        let thumbs = this.state.game.map((singleGame) => {
            return(


                <tr key={singleGame.id}>
                    <td>{singleGame.id}</td>
                    <td>{singleGame.name}</td>
                    <td>{singleGame.genre}</td>
                    <td>{singleGame.gameconsole}</td>
                    <td>
                        <Button color="success" size="sm" className="mr-2">Edit</Button>
                        <Button color="danger" size="sm">Delete</Button>
                    </td>
                </tr>


            )

            });



        return (
            <div className="gamedex">

<div className = "App container">
            <Button color="primary" onClick={this.toggleNewGameModal.bind(this)}>Add game</Button>
            <Modal isOpen={this.state.newGameModal} toggle={this.toggleNewGameModal.bind(this)}>
                <ModalHeader toggle={this.toggleNewGameModal.bind(this)}>Add a new Game</ModalHeader>
                <ModalBody>
                    <FormGroup>
                        <Label for="title">Titel</Label>
                        <Input id="title" value={this.state.newGameData.title} onChange={(e) => {
                            let { newGameData } = this.state;

                            newGameData.title = e.target.value;

                            this.setState({ newGameData});
                        }
                        } />
                    </FormGroup>

                <FormGroup>
                        <Label for="genre">Genre</Label>
                        <Input id="genre" value={this.state.newGameData.genre} onChange={(e) => {
                            let { newGameData} = this.state;

                            newGameData.genre = e.target.value;

                            this.setState({ newGameData});

                        } } />
                </FormGroup>

                <FormGroup>
                        <Label for="gameconsole">Gameconsole</Label>
                        <Input id="gameconsole" value={this.state.newGameData.gameconsole} onChange={(e) => {
                            let { newGameData } = this.state;

                            newGameData.gameconsole = e.target.value;
                            this.setState({ newGameData});
                        }}/>
                </FormGroup>

              </ModalBody>
              <ModalFooter>
                <Button color="primary" onClick={this.postData.bind(this)}>Add Game</Button>{' '}
                <Button color="secondary" onClick={this.toggleNewGameModal}>Cancel</Button>
              </ModalFooter>
            </Modal>
            </div>

                <Table>
                    <thead>
                        <tr>
                            {thumbs}
                        </tr>
                    </thead>
                </Table>
            </div>
        )   
    }
}
