import React, { Component } from "react";
import { Link } from "react-router-dom";
import Pagination from "./pagination";
class Home extends Component {
  constructor() {
    super();
    this.state = {
      items: [],
      pagination: null,
      links: null,
      start: 0,
      limit: 5
    };
    this.updateResults = this.updateResults.bind(this);
  }
  componentDidMount() {
    this.fetchResults();
  }
  async fetchResults() {
    const res = await fetch(
      `http://145.24.222.167:8000/games?start=${this.state.start}&limit=${this.state.limit}`
    );
    const json = await res.json();
    this.setState(json);
  }
  updateResults(startLimit) {
    this.setState(startLimit, this.fetchResults);
  }
  render() {
    let items = this.state.items.map((item, i) => (
      <li
        key={i}
        className="list-group-item d-flex justify-content-between align-items-center"
      >
        <span className="badge badge-primary badge-pill">{i + 1}</span>
        {item.name}
        <div className="btn-group" role="group" aria-label="Basic example">
          <Link to={item._id} className="btn btn-primary">
            View item
          </Link>
        </div>
      </li>
    ));

    return (
      <React.Fragment>
        <h1>Showing {this.state.items.length}</h1>
        <div className="row">
          <div className="col">
            <ul className="list-group">{items}</ul>
          </div>
        </div>
        <div className="row">
          <Pagination
            data={this.state.pagination}
            updateResults={this.updateResults}
            start={this.state.start}
            limit={this.state.limit}
          ></Pagination>
        </div>
      </React.Fragment>
    );
  }
}

export default Home;
