import React from 'react';
import { Card } from './Card';
import { Modal } from 'reactstrap'
import { ModalHeader } from 'reactstrap'
import { ModalBody } from 'reactstrap'
import { ModalFooter } from 'reactstrap'
import { Table } from 'reactstrap'
import { Button } from 'reactstrap'

export class GameDex extends React.Component {

    constructor() {
        super()
        this.state = { 
            game: []
        ,
        editGameData: {
            id: '',
            name: '',
            genre: '',
            gameconsole: ''
        }
    }
    }

    componentDidMount() {
        this.loadGame()
        }

    toggleNewBookModal() {
        this.setState({
            newGameModal: true
        });
    }

        async loadGame() {
            const response = await fetch(`http://145.24.222.167:8000/games`)
            const json = await response.json()
            console.log(json.items)
            this.setState({game: json.items})
        }    

        editGame(id, name, genre, gameconsole) {
            console.log(name)
        }

    render() {

        let thumbs = this.state.game.map((singleGame) => {
            return(
                <tr key={singleGame.id}>
                    <td>{singleGame.id}</td>
                    <td>{singleGame.name}</td>
                    <td>{singleGame.genre}</td>
                    <td>{singleGame.gameconsole}</td>
                    <td>
                        <Button color="success" size="sm" className="mr-2" 
                        onClick={this.editGame.bind(this, singleGame.id, singleGame.name,singleGame.genre,singleGame.gameconsole)}>
                            Edit</Button>
                    </td>
                </tr>
            )
            });

        return (
            <div className="gamedex">

                <Table>
                    <thead>
                        <tr>
                            {thumbs}
                        </tr>
                    </thead>
                </Table>
            </div>
        )   
    }
}
