import React from 'react';

export class Create extends React.Component {

        constructor() {
          super();
          this.state = {

          };

          this.handleChange = this.handleChange.bind(this);
          this.postData = this.postData.bind(this);
        }

        handleChange(e) {
          this.setState({ [e.target.name]: e.target.value });
        }

        async postData() {
          const response = await fetch(`http://145.24.222.167:8000/games`, {
            method: "POST",
            mode: 'cors',
            cache: 'no-cache',
            headers: { 
                "Content-Type": "application/json" 
            },
            body: JSON.stringify(this.state)
          });

          let myresult = await response.json();
          console.log(myresult)
        }



        render() {

          return (
            <form>
              <div className="row">
                <div className="col">
                    <h1>Add a game</h1>
                    <p> Name:
                        <input onChange={this.handleChange} name="name" type="text" id="name" 
                                    value={this.state.name ? this.state.name : ""}
                        ></input>
                    </p>

                  <p>
                    Genre:{" "}
                    <input
                      onChange={this.handleChange}
                      type="text"
                      name="genre"
                      value={this.state.genre ? this.state.genre : ""}
                      id="genre"
                      className="form-control"
                    ></input>
                  </p>
                  <p>
                    gameconsole:{" "}
                    <input
                      onChange={this.handleChange}
                      type="text"
                      name="gameconsole"
                      value={this.state.gameconsole ? this.state.gameconsole : ""}
                      id="gameconsole"
                      className="form-control"
                    ></input>
                  </p>
                  <p>
                    <input
                      type="postData"
                      className="btn btn-primary"
                      onClick={this.postData}
                      value="Create Game"
                    ></input>
                  </p>
                </div>
              </div>
              </form>
          );
        }
      }
