        }



        render() {
            let added;
            if (this.state.added) {
                added = (this.state.added);
            }

          return (
            <form>
                {added}
              <div className="row">
                <div className="col">
                  <h1>Add a game</h1>
                </div>
              </div>
              <div className="row">
                <br />
                <div className="col">
                  <p>
                    Name:{" "}
                    <input
                      onChange={this.handleChange}
                      type="text"
                      name="name"
                      value={this.state.name ? this.state.name : ""}
                      id="name"
                      className="form-control"
                    ></input>
                  </p>
                  <p>
                    Genre:{" "}
                    <input
                      onChange={this.handleChange}
                      type="text"
                      name="genre"
                      value={this.state.genre ? this.state.genre : ""}
                      id="genre"
                      className="form-control"
                    ></input>
                  </p>
                  <p>
                    gameconsole:{" "}
                    <input
                      onChange={this.handleChange}
                      type="text"
                      name="gameconsole"
                      value={this.state.gameconsole ? this.state.gameconsole : ""}
                      id="gameconsole"
                      className="form-control"
                    ></input>
                  </p>
                  <p>
                    <input
                      type="postData"
                      className="btn btn-primary"
                      onClick={this.postData}
                      value="Create Game"
                    ></input>
                  </p>
                </div>
              </div>
              </form>
          );
        }
      }

      export default Create;
